import { useState, useEffect } from "react";

function Book({id}) {
    const [book, setBook] = useState([]);

    useEffect(() => {
        fetch("http://127.0.0.1:8000/api/book/")
        .then(response => response.json())
        .then(books => {
            setBook(books.filter(book.id === id));
        })
        .catch(err => console.log(err))
    })

    const displayBook = () => {
            book.map(elem =>
                <li>{elem}</li>
            )
    }

    return (
        <ul>
            {displayBook()}
        </ul>
    )
}

export default Book;
