import { useState, useEffect } from "react";

function Book({id}) {
    const [book, setBook] = useState([]);

    useEffect(() => {
        fetch("http://127.0.0.1:8000/api/book/")
        .then(response => response.json())
        .then(books => {
            const single_book = books.filter(books =>books.id === id);

        })
        return (
            <ul>
                <li>
                    {single_book}
                </li>
            </ul>
        )
        .catch(err => console.log(err))
    })

}

export default Book;
