import { useState, useEffect } from "react";

function Book({bookid}) {
    const [book, setBook] = useState([]);

    useEffect(() => {
        let url = "http://127.0.0.1:8000/api/book/" + bookid + "/";
        fetch(url)
        .then(response => response.json())
        .then(data => {
            let bookInfo = setBook(data.map(book => <li>{book}</li>));
        })
        .catch(err => console.log(err))
    })

    return (
        <ul>
            <li>{bookInfo}</li>
        </ul>
    )
}

export default Book;
