import { useState, useEffect } from "react";

function Book({bookid}) {
    const [book, setBook] = useState([]);

    useEffect(() => {
        let url = "http://127.0.0.1:8000/api/book/" + bookid + "/";
        fetch(url)
        .then(response => response.json())
        .then(book => {
            setBook(book)
        })
        .catch(err => console.log(err))
    })

    const displayBook = () => {
        return (
            <div>
                <li>{book.title}</li>
                <li>{book.author}</li>
            </div>
        )
    }

    return (
        <ul>
            {displayBook()}
        </ul>
    )
}

export default Book;
