import { useState, useEffect } from "react";

function Book({id}) {
    const [book, setBook] = useState([]);

    useEffect(() => {
        fetch("http://127.0.0.1:8000/api/book/")
        .then(response => response.json())
        .then(books => {
            const single_book = books.filter(book => book.id === id);
            const listItems = single_book.map(person =>
            <li>
                <p>{single_book.title}</p>
                <p>{single_book.author}</p>
                <p>{single_book.genre}</p>
                <p>{single_book.release_year}</p>
                <p>{single_book.quantity}</p>
            </li>
            );
        })
        .catch(err => console.log(err))
    })

    return (
        <ul>
            {listItems}
        </ul>
    )
}

export default Book;
